import React, { useState } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Modal from '../../components/Shared/Modal';
import './CartPage.css';

function CartPage({ cart, setCart, removeFromCart }) {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedItem, setSelectedItem] = useState(null);

  // 총 가격 계산 함수
  const calculateTotalPrice = () => {
    return cart.reduce((total, item) => total + item.price * item.quantity, 0);
  };

  // 구매 개수 증가
  const increaseQuantity = (productId) => {
    setCart((prevCart) =>
      prevCart.map((item) =>
        item.id === productId ? { ...item, quantity: item.quantity + 1 } : item
      )
    );
  };

  // 구매 개수 감소
  const decreaseQuantity = (productId) => {
    setCart((prevCart) =>
      prevCart.map((item) =>
        item.id === productId && item.quantity > 1
          ? { ...item, quantity: item.quantity - 1 }
          : item
      )
    );
  };

  const handleRemoveClick = (item) => {
    setSelectedItem(item);
    setIsModalOpen(true);
  };

  const handleConfirmRemove = () => {
    if (selectedItem) {
      removeFromCart(selectedItem.id);
      toast.success(`${selectedItem.name}이(가) 삭제되었습니다.`);
    }
    setIsModalOpen(false);
    setSelectedItem(null);
  };

  return (
    <div className="cart-page">
      <ToastContainer />
      <Modal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        onConfirm={handleConfirmRemove}
        message={`${selectedItem?.name}을(를) 삭제하시겠습니까?`}
      />
      <h1>카트</h1>
      {cart.length === 0 ? (
        <p className="empty-cart">카트에 담긴 상품이 없습니다.</p>
      ) : (
        <>
          <ul className="cart-list">
            {cart.map((item) => (
              <li key={item.id} className="cart-item">
                <img
                  src={require(`../../assets/images/${item.image}`)}
                  alt={item.name}
                  className="cart-item-image"
                />
                <div className="cart-item-details">
                  <h3 className="cart-item-name">{item.name}</h3>
                  <p className="cart-item-price">{item.price.toLocaleString()}원</p>
                  <div className="cart-item-quantity">
                    <button
                      className="quantity-button"
                      onClick={() => decreaseQuantity(item.id)}
                    >
                      -
                    </button>
                    <span>{item.quantity}</span>
                    <button
                      className="quantity-button"
                      onClick={() => increaseQuantity(item.id)}
                    >
                      +
                    </button>
                  </div>
                  <p className="cart-item-description">{item.description}</p>
                </div>
                <button
                  className="cart-item-remove-button"
                  onClick={() => handleRemoveClick(item)}
                >
                  제거
                </button>
              </li>
            ))}
          </ul>
          {/* 총 가격 표시 */}
          <div className="cart-total">
            <h2>총 가격: {calculateTotalPrice().toLocaleString()}원</h2>
          </div>
        </>
      )}
    </div>
  );
}

export default CartPage;
