import React, { useState } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Modal from '../../components/Shared/Modal';
import './CartPage.css';

function CartPage({ cart, setCart, removeFromCart, theme }) {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedItem, setSelectedItem] = useState(null);

  const calculateTotalPrice = () =>
    cart.reduce((total, item) => total + item.price * item.quantity, 0);

  const updateQuantity = (productId, change) => {
    setCart((prevCart) =>
      prevCart.map((item) =>
        item.id === productId && (item.quantity + change > 0)
          ? { ...item, quantity: item.quantity + change }
          : item
      )
    );
  };

  const handleRemoveClick = (item) => {
    setSelectedItem(item);
    setIsModalOpen(true);
  };

  const handleConfirmRemove = () => {
    if (selectedItem) {
      removeFromCart(selectedItem.id);
      toast.success(`${selectedItem.name}이(가) 삭제되었습니다.`);
    }
    setIsModalOpen(false);
    setSelectedItem(null);
  };

  const handleCheckout = () => {
    if (cart.length === 0) {
      toast.error('카트에 상품이 없습니다.');
      return;
    }
    toast.success(
      `총 결제 금액은 ${calculateTotalPrice().toLocaleString()}원입니다.\n결제가 완료되었습니다.`
    );
    setCart([]);
    localStorage.removeItem('cart');
  };

  return (
    <div className={`cart-page ${theme}`}>
      <ToastContainer />
      <Modal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        onConfirm={handleConfirmRemove}
        message={`${selectedItem?.name}을(를) 삭제하시겠습니까?`}
        theme={theme}
      />
      <h1 className="cart-title">CART</h1>
      {cart.length === 0 ? (
        <p className="empty-cart">카트에 담긴 상품이 없습니다.</p>
      ) : (
        <>
          <ul className="cart-list">
            {cart.map((item) => (
              <li key={item.id} className={`cart-item ${theme}`}>
                <img
                  src={require(`../../assets/images/${item.image}`)}
                  alt={item.name}
                  className="cart-item-image"
                />
                <div className="cart-item-details">
                  <h3 className="cart-item-name">{item.name}</h3>
                  <p className="cart-item-price">
                    {item.price.toLocaleString()}원
                  </p>
                  <div className="cart-item-quantity">
                    <button
                      className="quantity-button"
                      onClick={() => updateQuantity(item.id, -1)}
                    >
                      -
                    </button>
                    <span>{item.quantity}</span>
                    <button
                      className="quantity-button"
                      onClick={() => updateQuantity(item.id, 1)}
                    >
                      +
                    </button>
                  </div>
                  <p className="cart-item-description">{item.description}</p>
                </div>
                <button
                  className="cart-item-remove-button"
                  onClick={() => handleRemoveClick(item)}
                >
                  제거
                </button>
              </li>
            ))}
          </ul>
          <div className="cart-total">
            <h2>총 가격: {calculateTotalPrice().toLocaleString()}원</h2>
          </div>
          <button className="checkout-button" onClick={handleCheckout}>
            결제하기
          </button>
        </>
      )}
    </div>
  );
}

export default CartPage;
