import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import MainPage from './pages/MainPage/MainPage';
import CartPage from './pages/CartPage/CartPage';
import Header from './components/Shared/Header'; // Header 컴포넌트 가져오기

function App() {
  // 1. 카트 상태 정의
  const [cart, setCart] = useState([]);

  // 2. 카트에 상품 추가 함수 정의
  const addToCart = (product) => {
    setCart((prevCart) => {
      // 중복 확인 (이미 추가된 상품이면 무시)
      if (prevCart.find((item) => item.id === product.id)) {
        return prevCart;
      }
      return [...prevCart, product]; // 기존 카트에 새 상품 추가
    });
  };

  return (
    <Router>
      {/* 공통 네비게이션 추가 */}
      <Header />
      <Routes>
        {/* MainPage에 addToCart 함수 전달 */}
        <Route path="/" element={<MainPage addToCart={addToCart} />} />
        {/* CartPage에 cart 데이터 전달 */}
        <Route path="/cart" element={<CartPage cart={cart} />} />
      </Routes>
    </Router>
  );
}

export default App;
